//Casting Example 

package InClassWorkDayTwo;

public class InClassWorkDayTwo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		double z = 2333333366555555555665555555555555555333d;
		//assigning z
		
		float d = (float)z;
		//casting
		
		System.out.println(d);
		//this gives infinity because z is way too big
		
		
	}

}
